import java.util.Scanner;
public class NoOfDaysInAMonth {
	public static void main(String args []){ 
	//create scanner 
	Scanner input = new Scanner(System.in); 
	System.out.print("Enter a year:"); 
	int year = input.nextInt(); //Moved here to get input after the question is asked 

	System.out.print("Enter a month:"); 
	int month = input.nextInt(); //Moved here to get input after the question is asked 

	int days = 0; //changed so that it just initializes the variable to zero 


	boolean isLeapYear = (year % 4 == 0 && year % 100 != 0)||(year % 400 == 0); 
	switch (month){ 
	case 1: 
	days = 31; 
	break; 
	case 2: 
	if (isLeapYear) 
	days = 29; 
	else 
	days = 28; 
	break; 
	case 3: 
	days = 31; 
	break; 
	case 4: 
	days = 30; 
	break; 
	case 5: 
	days = 31; 
	break; 
	case 6: 
	days = 30; 
	break; 
	case 7: 
	days = 31; 
	break; 
	case 8: 
	days = 31; 
	break; 
	case 9: 
	days = 30; 
	break; 
	case 10: 
	days = 31; 
	break; 
	case 11: 
	days = 30; 
	break; 
	case 12: 
	days = 31; 
	break; 
	default: 
	if ((month < 1)||(month > 12)) 
	System.exit(0); 

	} 
	//moved here so that the answer is printed after days has been calculated 
	String response = "There are " +days+ "days in month "+month+ " of " +year+ ".\n"; 
	System.out.println(response); // new line to show the result to the screen. 
	} 
	}
